pragma solidity ^0.4.24;

import "./interfaces/IAirDrop.sol";
import "@cpchain-tools/cpchain-dapps-utils/contracts/token/ERC20/ERC20.sol";
import "@cpchain-tools/cpchain-dapps-utils/contracts/utils/math/SafeMath.sol";
import "@cpchain-tools/cpchain-dapps-utils/contracts/lifecycle/Enable.sol";

contract AirDrop is IAirDrop, Enable, ERC20("CpchainAirDropToken", "CAT", 18, 0) {

    using SafeMath for uint256;

    address owner; // owner has permissions to modify parameters

    uint256 public airdrop_amount = 5 ether;
    uint256 public reward = 5 ether;

    uint public reward_cnt_limit = 100;

    uint256 public recharge_token_radix = 100 ether; // 决定计算充值金额所得代币奖励数量的函数的增长幅度的参数
    uint256 public recharge_token_coefficient = 10; // 比例系数，函数计算的结果乘以该值则为最后获得的奖励值

    uint256 public airdrop_token_radix = 100; // 决定计算邀请次数对应的代币奖励数量的函数的增长幅度的参数
    uint256 public airdrop_token_coefficient = 1 ether; // 比例系数，函数计算的结果乘以该值
    uint256 public airdrop_token_constant = 1 ether; // 邀请奖励的基数，可以为0，即最少获得该数量的代币

    struct Receiver {
        address receiver;
        bool received; // every address can only receiver airdrop once
        uint reward_cnt;
    }

    mapping(address => Receiver) internal receivers;

    mapping(address => bool) internal managers;
    
    constructor() public {
        owner = msg.sender;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "You're not the owner of this contract");
        _;
    }

    modifier onlyManager() {
        require(managers[msg.sender] == true, "You're not the owner of this contract");
        _;
    }


    modifier onlyEnabled() {
        require(enabled, "The Contract is disabled");
        _;
    }

    function isManager(address user) public view returns(bool) {
        return managers[user];
    }

    function tokenForNumber(uint number) public view returns(uint256) {
        uint256 covert = 10 ** uint256(9);
        uint256 denominator = number + airdrop_token_radix;
        uint256 numerator = airdrop_token_radix.mul(covert);
        uint256 token_for_number = numerator.div(denominator);
        uint256 coefficient = airdrop_token_coefficient.div(covert);
        token_for_number = token_for_number.mul(coefficient).add(airdrop_token_constant);

        return(token_for_number);
    }

    /** 用于计算充值金额对应的代币奖励，具体计算方式为 y = 10 * (x/(x + 100)),其中参数可以改变 */
    function tokenForAmount(uint256 amount) public view returns(uint256) {
        uint256 covert = 10 ** uint256(9);
        uint256 denominator = amount + recharge_token_radix; // 方程的分母 即为 x + 100 的替换
        denominator = denominator.div(covert); // 精确到小数点后九位
        uint256 token_for_amount = amount.div(denominator); // 相除
        token_for_amount = token_for_amount.mul(covert); 
        token_for_amount = token_for_amount.mul(recharge_token_coefficient); // 乘以比例系数
        return token_for_amount;
    }

    /** change the amount in each airdrop */
    function setAirDropAmount(uint256 new_airdrop_amount) public onlyEnabled onlyOwner {
        require(new_airdrop_amount >= 1 ether, "The amount can not be less than 1");
        require(new_airdrop_amount <= 100 ether, "The amount can not be greater than 100");
        airdrop_amount = new_airdrop_amount;
    }

    /** change the reward for user invited newuser */
    function setReward(uint256 new_reward) public onlyEnabled onlyOwner {
        require(new_reward >= 1 ether, "The reward can not be less than 1");
        require(new_reward <= 100 ether, "The reward can not be greater than 100");
        reward = new_reward;
    }

    /** Change the maximum number of times each address can get a reward */
    function setRewardCntLimit(uint new_reward_cnt_limit) public onlyEnabled onlyOwner {
        require(new_reward_cnt_limit >= 1, "The limit can not be less than 1");
        require(new_reward_cnt_limit <= 10000, "The limit can not be greater than 10000");
        reward_cnt_limit = new_reward_cnt_limit;
    }

    function setManager(address _manager) public onlyEnabled onlyOwner {
        require(managers[_manager] == false, "This address was already manager");
        managers[_manager] = true;
    }

    function removeManager(address _manager) public onlyEnabled onlyOwner {
        require(managers[_manager] == true, "This address was no longer the manager");
        managers[_manager] = false;
    }

    function balanceOfCAT(address account) public view returns (uint256) {
        return this.balanceOf(account);
    }

    /**
     * 
     * @param target_addr 空投目标对象
     * @param inviter 空投目标邀请人
     */
    function provideAirDrop(address target_addr, address inviter) external payable onlyEnabled onlyManager {
        require(target_addr != inviter, "target_addr cannot be inviter");
        require(address(this).balance > airdrop_amount * 2, "balance of this contract is not enough to airdrop");
        receivers[target_addr].received = true;
        target_addr.transfer(airdrop_amount);
        uint256 target_airdrop_token = this.tokenForNumber(1);
        ERC20._mint(target_addr, target_airdrop_token);
        uint256 real_reward = 0;
        if (inviter != address(0x0)) {
            if (receivers[inviter].reward_cnt < reward_cnt_limit) {
                receivers[inviter].reward_cnt += 1;
                real_reward = reward;
                inviter.transfer(reward);

                uint256 inviter_airdrop_token = this.tokenForNumber(receivers[inviter].reward_cnt);
                ERC20._mint(inviter, inviter_airdrop_token);
            }
        }
        emit ProvideAirDrop(msg.sender, target_addr, inviter, airdrop_amount, real_reward); //emit event
    }


    function recharge() external payable {
        uint256 recharge_token = this.tokenForAmount(msg.value);
        ERC20._mint(msg.sender, recharge_token);
        emit Recharge(msg.sender, msg.value);
    }

    function getContractBalance() public constant returns(uint) {
        return address(this).balance;
    }

}
