// SPDX-License-Identifier: MIT
pragma solidity ^0.4.24;

import "./interfaces/IHello.sol";

contract Hello is IHello{
  address owner;

  uint public hellotime = 1;

  struct Helloed {
    address helloer;
    bool ishello;
  }

  mapping(address => Helloed) internal allhelloed;

  constructor() public {
    owner = msg.sender;
  }

  function helloToEveryOne() external {
      hellotime += 1;
      emit HelloToEveryOne(hellotime);
  }

  function helloToSomeOne(address target) external {
      allhelloed[target].ishello = true;
      emit HelloToSomeOne(target);
  }
}
